                highlightRow = TRUE
              ) %>%
                hot_col(1, 
                        valign = "htMiddle",
                        halign = "htCenter") %>%
                hot_col(3, readOnly = TRUE) %>%
                hot_col(c(1, 5, 10, 11, 12, 13),
                        readOnly = TRUE) %>%
