    }
  })
  
  # Show Label Panels?
  nj_geom <- reactive({
    if(input$nj_geom == TRUE) {
      "label"
    } else {"text"}
  })
  
  # NJ Tiplab color
  nj_mapping_tiplab <- reactive({
    if(input$nj_mapping_show == TRUE) {
      if(!is.null(input$nj_tiplab)) {
