          max <- 1200
          step <- 5
        } else if(input$gs_ratio == "1.77777777777778") {
          min <- 504
          max <- 1197
          step <- 9
        } else if(input$gs_ratio == "1.33333333333333") {
          min <- 501
          max <- 1200
          step <- 3
        }
      } else {
        gs_ratio_selected <- c("16:9" = (16/9))
        min <- 504
        max <- 1197
