          noclass_profile_matrix <- heatmap_mat[,colnames(heatmap_mat) %in% unclass_genes]
          
          if((all(is.na(hm_meta$amr)) | length(gs_plot_selected_amr) < 3) & (all(is.na(hm_meta$vir)) | length(gs_plot_selected_vir) < 3)) {
